import React, { useState } from "react";

const Popup = (props) => {
  // window width/height on resize courtesy of Jake Trent
  // https://www.pluralsight.com/guides/re-render-react-component-on-window-resize
  const [dimensions, setDimensions] = React.useState({
    height: window.innerHeight,
    width: window.innerWidth,
  });
  React.useEffect(() => {
    function handleResize() {
      setDimensions({
        height: window.innerHeight,
        width: window.innerWidth,
      });
    }

    window.addEventListener("resize", handleResize);

    return (_) => {
      window.removeEventListener("resize", handleResize);
    };
  });

  // Omaa koodia
  const [nimi, setNimi] = useState("");
  const [salasana, setSalasana] = useState("");
  const nappiStyles = "btn btn-secondary";
  const inputStyles = { width: "100%" };
  const closeStyles = {
    content: "x",
    cursor: "pointer",
    position: "absolute",
    right: "calc(15% - 30px)",
    top: "5px",
    background: "#ededed",
    width: "25px",
    height: "25px",
    borderRadius: "50%",
    lineHeight: "20px",
    textAlign: "center",
    border: "1px solid #999",
    fontSize: "20px",
  };
  const boxStyles = {
    position: "fixed",
    background: "#00000050",
    width: "100%",
    height: "100vh",
    top: "0",
    left: "0",
  };
  const popupStyles = {
    position: "relative",
    width: "300px",
    margin: "0 auto",
    height: "450px",
    maxheight: "calc(100vh - 85vh - 20px)",
    margintop: "15vh",
    background: "#fff",
    borderradius: "4px",
    paddingTop: "40px",
    paddingLeft: "40px",
    paddingRight: "40px",
    border: "2px solid #999",
    overflow: "auto",
  };

  return (
    <div style={boxStyles}>
      <div style={popupStyles}>
        <span style={closeStyles} onClick={props.handleClose}>
          x
        </span>
        <h1 style={{ fontSize: "30px" }}>Uusi Kalenteri</h1>
        <div>Kalenterin Nimi</div>
        <input // Kalenterin nimen input kenttä
          type="text"
          placeholder="Esimerkki Kalenteri"
          onInput={(e) => setNimi(e.target.value)}
          style={{ width: "100%" }}
        ></input>

        <div>Salasana</div>
        <input // Salasanan input kenttä
          type="text"
          placeholder="********"
          onInput={(e) => setSalasana(e.target.value)}
          style={{ width: "100%" }}
        ></input>

        <div>
          <button
            className={nappiStyles}
            style={{ marginTop: "190px", float: "right" }} // Kalenterin luonti nappi
            type="button"
            //onClick={<funktio>} // TODO: Lähetä Inputin arvo parametrina backendiin
          >
            Luo Kalenteri
          </button>
        </div>
      </div>
    </div>
  );
};
export default Popup;
