import getCalendar from './services/getCalendar'
import {useState} from 'react'
import FetchCalendarForm from './components/FetchCalendarForm'
import calendarService from './services/calendars'
import calendarLoginService from './services/calendarLogin'

const App = () => {
  //tänne tallennetaan haettu kalenteri tekstinä
  const [kasiteltavaKalenteri, setKasiteltavaKalenteri] = useState('') 
  //url laatikkoa varten
  const [kalenteriUrl, setUrl] = useState('') 
  //Asetetaan kalenteri token onnistuneen kirjautumisen jälkeen 
  //myöhempää tunnistautumista varten
  const [calendarToken, setCalendarToken] = useState('')
  const handleDownload = (event) => {
    event.preventDefault()
    getCalendar
      .download(kalenteriUrl, setKasiteltavaKalenteri)
      console.log("kalenteri: " + kasiteltavaKalenteri)
  }
    //testei vaa
  const testi = async () => {
    const testiPrivateCalendar = {
      "freeTimes": [
          {
          "id": "1",
          "start": "2023-03-07T10:30:00",
          "end": "2023-03-07T13:00:00"
        },
        {
          "id": "2",
          "start": "2023-03-08T09:30:00",
          "end": "2023-03-08T11:30:00"
        },
        {
          "id": "3",
          "start": "2023-03-08T12:00:00",
          "end": "2023-03-08T15:00:00"
        },
        {
          "id": "4",
          "start": "2023-03-06T11:30:00",
          "end": "2023-03-06T14:30:00"
        }
      ]
  }
    const salasana = "salainen"
    //luodaan uusi jaettu kalenteri
    const uudenLuodunKalenterinID = await calendarService.createSharedCalendar(salasana)
    //credentials kirjautmista varten
    const credentials = {sharedCalendarID: uudenLuodunKalenterinID, password: salasana}
    //kirjaudutaan kalenteriin ja vastaanotetaan token
    const loginInfo = await calendarLoginService.calendarLogin(credentials)
    console.log(loginInfo)
    //asetetaan token! 
    const token = loginInfo.token
    calendarService.setToken(token + "asd")
    //lisätään uuteen jaettuun kalenteriin private kalenteri
    const response = await calendarService.createPrivateCalendar(testiPrivateCalendar, uudenLuodunKalenterinID)
    console.log(response)
  }
  testi()
  //demon vuoksi laitetaan kasiteltavaKalenteri näkyviin sivulle
  return (
    <div>
      <div>
    {/* <FetchCalendarForm 
    kalenteriUrl={kalenteriUrl} 
    handleKalenteriUrlChange={({ target }) => setUrl(target.value)} 
    handleFetchCalendar={handleDownload}
    /> */}
    </div>
  <p>
    {kasiteltavaKalenteri} 
  </p>
  </div>
  )
}

export default App
