import React, { useState } from "react";

const Popup = (props) => {
  const [nimi, setNimi] = useState("");
  const [salasana, setSalasana] = useState("");
  const nappiStyles = "btn btn-secondary";
  const inputStyles = { width: "100%" };
  const closeStyles = {
    content: "x",
    cursor: "pointer",
    position: "absolute",
    right: "calc(15% - 30px)",
    top: "5px",
    background: "#ededed",
    width: "25px",
    height: "25px",
    borderRadius: "50%",
    lineHeight: "20px",
    textAlign: "center",
    border: "1px solid #999",
    fontSize: "20px",
  };
  const boxStyles = {
    position: "fixed",
    background: "#00000050",
    width: "100%",
    height: "100vh",
    top: "0",
    left: "0",
  };
  const popupStyles = {
    position: "relative",
    width: "300px",
    margin: "0 auto",
    height: "450px",
    maxheight: "70vh",
    margintop: "15vh",
    background: "#fff",
    borderradius: "4px",
    paddingTop: "40px",
    paddingLeft: "40px",
    paddingRight: "40px",
    border: "2px solid #999",
    overflow: "auto",
  };

  return (
    <div style={boxStyles}>
      <div style={popupStyles}>
        <span style={closeStyles} onClick={props.handleClose}>
          x
        </span>
        {props.content}
      </div>
    </div>
  );
};
export default Popup;
