import React, { useState } from "react";

function Navbar() {
  // window width/height on resize courtesy of Jake Trent
  // https://www.pluralsight.com/guides/re-render-react-component-on-window-resize
  const [dimensions, setDimensions] = React.useState({
    height: window.innerHeight,
    width: window.innerWidth,
  });
  React.useEffect(() => {
    function handleResize() {
      setDimensions({
        height: window.innerHeight,
        width: window.innerWidth,
      });
    }

    window.addEventListener("resize", handleResize);

    return (_) => {
      window.removeEventListener("resize", handleResize);
    };
  });

  // Omaa koodia
  const [haku, setHaku] = useState("");
  const nappiStyles = "btn btn-outline-secondary";

  return (
    <div
      className="input-group"
      style={{
        backgroundColor: "lightBlue",
        padding: "10px",
        borderStyle: "solid",
        border: "1px",
      }}
    >
      <input // Hae kalenteria input kenttä
        type="text"
        placeholder="Hae Kalenteria"
        aria-label="Hae Kalenteria"
        onInput={(e) => setHaku(e.target.value)}
      ></input>

      <div className="input-group-append">
        <button // Kalenterin haku nappi
          className={nappiStyles}
          type="button"
          //onClick={<funktio>} // TODO: Lähetä Inputin arvo parametrina backendiin
        >
          Hae
        </button>
      </div>

      <button // Uusi kalenteri nappi
        className={nappiStyles}
        type="button"
        //onClick={<funktio>} // TODO: Avaa pupup ikkuna
        style={{
          width: 120,
          marginLeft: dimensions.width - 150,
          position: "absolute",
        }}
      >
        Uusi kalenteri
      </button>
      <div>{haku}</div>
    </div>
  );
}
export default Navbar;
